import "vant/es/toast/style";
import _Toast from "vant/es/toast";
import "vant/es/uploader/style";
import _Uploader from "vant/es/uploader";
import "vant/es/icon/style";
import _Icon from "vant/es/icon";
import "core-js/modules/es.array.includes.js";
import "core-js/modules/es.array.push.js";
import "core-js/modules/es.object.to-string.js";
import "core-js/modules/es.regexp.exec.js";
import "core-js/modules/es.string.replace.js";
import QRCode from 'qrcode';
import { priceFormat } from '@/utils/utis';
import { chongzhitijiao_post, session_token, tupianshangchuan_post, zhifutongdao_post } from "@/API/user";
export default {
  data() {
    return {
      priceFormat,
      itemName: process.env.VUE_APP_ITEM_NAME,
      data: {},
      codes: '',
      tongdao_xuanzhong: {},
      imgUrl: null,
      tongdao: [],
      it: {
        input1: ''
      },
      fileList: [],
      shangchuanurl: '',
      session_token: ''
    };
  },
  components: {
    QRCode: QRCode,
    [_Icon.name]: _Icon,
    [_Uploader.name]: _Uploader
  },
  created() {
    // this.$nextTick(function () {
    //   this.useqrcode1()
    // })
    // this.useqrcode1()\
    this.zhifutongdao();
  },
  methods: {
    handleInput() {
      this.it.input1 = this.it.input1.replace(/[^\d.]/g, ""); // 非数字和小数点替换为空字符串
      this.it.input1 = this.it.input1.replace(/\.{2,}/g, "."); // 连续出现多个小数点替换为单个小数点
      this.it.input1 = this.it.input1.replace(/^\./g, ""); // 第一个字符如果是小数点则替换为空字符串
      this.it.input1 = this.it.input1.replace(".", "$#$").replace(/\./g, "").replace("$#$", "."); // 将第一个小数点替换为$#$，去除所有小数点，再将$#$替换回小数点
      if (['BTC', 'ETH'].includes(this.$route.query.type)) {
        this.it.input1 = this.it.input1.replace(/^(\-)*(\d+)\.(\d{0,10}).*$/, "$1$2.$3"); // 最多允许输入10位小数
        this.it.input1 = this.it.input1.replace(/^\D*(\d{0,10}(?:\.\d{0,10})?).*$/g, "$1"); // 最多允许输入10位数字
      } else {
        this.it.input1 = this.it.input1.replace(/^(\-)*(\d+)\.(\d{0,2}).*$/, "$1$2.$3"); // 最多允许输入2位小数
        // this.it.input1 = this.it.input1.replace(/^\D*(\d{0,2}(?:\.\d{0,10})?).*$/g, "$1"); // 最多允许输入10位数字
      }
    },
    biaodan_tijiao() {
      var _this = this;
      var t = this;
      if (this.it.input1 === '') {
        _Toast(t.$t('请输入充值数量'));
        return;
      }
      if (this.shangchuanurl === '') {
        _Toast(t.$t('请上传图片'));
        return;
      }
      if ((this.it.input1 * this.tongdao_xuanzhong.fee).toFixed(2) < this.tongdao_xuanzhong.recharge_limit_min) {
        _Toast(t.$t('充值价值不得小于最小限额') + this.tongdao_xuanzhong.recharge_limit_min + 'usdt');
        return;
      }
      if ((this.it.input1 * this.tongdao_xuanzhong.fee).toFixed(2) > this.tongdao_xuanzhong.recharge_limit_max) {
        _Toast(t.$t('充值价值不得大于最大限额') + this.tongdao_xuanzhong.recharge_limit_max + 'usdt');
        return;
      }
      session_token({}).then(function (res) {
        _this.session_token = res.session_token;
        _this.chongzhitijiao();
      });
    },
    chongzhitijiao() {
      var _this2 = this;
      var data = {
        'session_token': this.session_token,
        'amount': this.it.input1,
        'from': '123',
        'blockchain_name': this.tongdao_xuanzhong.blockchain_name,
        'img': this.shangchuanurl,
        'coin': this.tongdao_xuanzhong.coin,
        'channel_address': this.tongdao_xuanzhong.address,
        'tx': '123'
      };
      _Toast.loading({
        duration: 0,
        forbidClick: true,
        loadingType: "spinner",
        message: ""
      });
      chongzhitijiao_post(data).then(function (res) {
        _Toast.clear();
        _Toast(_this2.$t('提交成功'));
        _this2.it.input1 = '';
        _this2.fileList = [];
        _this2.shangchuanurl = '';
        _this2.$router.push('/SubmitCompleted?path=rechargeRecord');
      }).catch(function (err) {
        // Toast.clear()
        // reject(err)
        _Toast.clear();
        // Toast(this.$t(err.msg));
      });
    },
    tongdao_qiehuan(e) {
      this.tongdao_xuanzhong = e;
      this.useqrcode1();
    },
    zhifutongdao() {
      var _this3 = this;
      zhifutongdao_post({}).then(function (res22) {
        // this.code = this.$route.query.usercode

        for (var i = 0; i < res22.length; i++) {
          if (res22[i]['coin'] === _this3.$route.query.type) {
            _this3.tongdao.push(res22[i]);
          }
        }
        _this3.tongdao_xuanzhong = _this3.tongdao[0];
        _this3.useqrcode1();
      });
    },
    afterRead(file) {
      var _this4 = this;
      _Toast.loading({
        duration: 0,
        forbidClick: true,
        loadingType: "spinner",
        message: ""
      });
      var formData = new FormData(); //通过formdata上传
      formData.append('file', file.file);
      formData.append('moduleName', "recharge");
      tupianshangchuan_post(formData).then(function (res) {
        _Toast.clear();
        _this4.shangchuanurl = res;
      }).catch(function (err) {
        _Toast.clear();
        _Toast(this.$t('添加失败'));
        this.fileList = [];
      });
    },
    copyData(ea) {
      var _this5 = this;
      //如果需要回调：
      this.$copyText(ea).then(function (e) {
        _Toast(_this5.$t('复制成功'));
      }, function (e) {
        _Toast(this.$t('复制失败'));
      });
    },
    copyData2(ea) {
      //如果需要回调：
      this.$copyText(ea).then(function (e) {
        // alert('复制成功')
      }, function (e) {
        // alert('复制失败')
      });
    },
    useqrcode1() {
      //生成二维码
      var canvas = document.getElementById('canvas');
      var url = this.tongdao_xuanzhong.address;
      QRCode.toCanvas(canvas, url, function (error) {
        if (error) {} else {}
      });
      this.saveImg(); //保存图片
    },
    baocunhaibao() {
      var myCanvas = document.getElementsByTagName('canvas');
      this.imgUrl = myCanvas[0].toDataURL('image/png');
      this.getUrlBase64(this.imgUrl).then(function (base64) {
        var link = document.createElement('a');
        link.href = base64;
        link.download = 'qrCode.png';
        link.click();
      });
    },
    getUrlBase64(url) {
      return new Promise(function (resolve) {
        var canvas = document.createElement('canvas');
        var ctx = canvas.getContext('2d');
        var img = new Image();
        img.crossOrigin = 'Anonymous'; //允许跨域
        img.src = url;
        img.onload = function () {
          canvas.height = 300;
          canvas.width = 300;
          ctx.drawImage(img, 0, 0, 300, 300);
          var dataURL = canvas.toDataURL('image/png');
          canvas = null;
          resolve(dataURL);
        };
      });
    },
    //保存图片
    saveImg() {
      var myCanvas = document.getElementsByTagName('canvas');
      this.imgUrl = myCanvas[0].toDataURL('image/png');
    },
    onClickLeft() {
      this.$router.push({
        path: '/rechargeList',
        dir: 'left'
      });
    },
    onClick1() {
      this.$router.push({
        path: '/rechargeRecord'
      });
    }
  }
};
