import "vant/es/icon/style";
import _Icon from "vant/es/icon";
import "vant/es/toast/style";
import _Toast from "vant/es/toast";
import Vcode from "vue-puzzle-vcode";
import loginAxios from "@/API/login";
import NationalityList from "@/components/NationalityList";
import { isLogin } from "@/utils/utis";
export default {
  name: "LoginPage",
  data() {
    return {
      isShelves: !process.env.VUE_APP_PUT_ON_SHELVES,
      itemName: process.env.VUE_APP_ITEM_NAME,
      TabShow: 2,
      it: {
        input1: "",
        input2: ""
      },
      show: false,
      img1: require("@/assets/image/slider/1.png"),
      img2: require("@/assets/image/slider/2.png"),
      img3: require("@/assets/image/slider/3.png"),
      img4: require("@/assets/image/slider/4.png"),
      img5: require("@/assets/image/slider/5.png"),
      triangleDown: require("@/assets/image/me/triangle_down.png"),
      pageDiaCode: false,
      dialCode: localStorage.getItem("dialCode") || 1
    };
  },
  components: {
    [_Toast.name]: _Toast,
    [_Icon.name]: _Icon,
    Vcode,
    NationalityList
  },
  created() {
    if (isLogin() && this.$route.query.from == "ldy") {
      this.$notify(this.$t('您已登录'), 'success');
      this.$router.push({
        path: "/"
      });
    }
  },
  methods: {
    handleJump() {
      const path = this.$store.state.multiItem[process.env.VUE_APP_ITEM_NAME].customer_service;
      if (path) {
        if (window.plus) {} else {
          window.open(path);
        }
      } else {
        this.$router.push({
          path: "customerServiceIndex?path=customerService"
        });
      }
      // var url = 'https://ld.ebvsjbv.site/chat/#/customerServiceH5?lang='+this.$i18n.locale
      // if (localStorage.getItem('token')){
      //   url+='&token='+localStorage.getItem('token')
      // }
      // window.location.href=url
    },
    // gotoForget() {
    //     this.$router.push({ path: "forgetPassword" });
    // },
    TabClick(e) {
      this.it = {
        input1: "",
        input2: ""
      };
      this.TabShow = e;
    },
    post_form() {
      const t = this;
      if (this.TabShow === 1) {
        if (this.it.input1 === "") {
          _Toast(t.$t("请输入邮箱"));
          return;
        } else {
          const reg = /^([A-Za-z0-9_\-\.\w{3,}])+\@([A-Za-z0-9_\-\.])+\.([A-Za-z]{2,4})$/;
          if (!reg.test(this.it.input1)) {
            _Toast(t.$t("邮箱格式有误"));
            return;
          }
        }
      }
      if (this.TabShow === 2) {
        if (this.it.input1 === "") {
          _Toast(t.$t("请输入手机号"));
          return;
        } else {
          const reg = /^[0-9]+[0-9]*$/;
          if (!reg.test(this.it.input1)) {
            _Toast(t.$t("手机号格式有误"));
            return;
          }
        }
      }
      if (this.it.input2 === "") {
        _Toast(t.$t("请输入密码"));
        return;
      }
      if (this.itemName == 'Hive') {
        this.loginSubmit();
      } else {
        this.show = true;
      }
    },
    onSuccess() {
      this.loginSubmit();
      this.show = false;
    },
    onFail() {
      console.log("失败回调");
    },
    loginSubmit() {
      const params = {
        password: encodeURIComponent(this.it.input2)?.trim()
      };
      params.username = this.TabShow === 1 ? this.it.input1?.trim() : `${this.dialCode} ${this.it.input1?.trim()}`;
      _Toast.loading({
        duration: 0,
        message: this.$t("提交中"),
        forbidClick: true
      });
      loginAxios.zhanghaodenglu(params).then(async res => {
        _Toast.clear();
        // Toast.success(this.$t("登录成功"));
        this.$notify(this.$t("登录成功"), 'success');
        localStorage.setItem("token", res.token);
        let timeZone = this.dayjs.tz.guess();
        localStorage.setItem("timeZone", timeZone);
        await this.$store.dispatch("getUserInfo");
        if (this.$route.query.from == "ldy") {
          this.$router.push({
            path: '/'
          });
          return;
        }
        try {
          if ('/register' == sessionStorage.getItem('upperLevel')) {
            this.$router.push({
              path: "/"
            });
          } else {
            this.$router.go(-1);
          }
        } catch (error) {
          this.$router.push({
            path: "/"
          });
        }
      }).catch(err => {
        _Toast.clear();
      });
    },
    getName(params) {
      if (!this.pageDiaCode) {
        this.pageDiaCode = true;
      } else {
        this.dialCode = params[2];
        localStorage.setItem("dialCode", this.dialCode);
      }
    },
    openNational() {
      this.$refs.controlChild.open();
    },
    onClickLeft() {
      this.$router.push('/');
    }
  }
};
