          flex: none;
  height: 5px;
  line-height: 0;
}
@-webkit-keyframes load {
0% {
    opacity: 1;
    -webkit-transform: scale(1.3);
            transform: scale(1.3);
}
100% {
    opacity: 0.2;
    -webkit-transform: scale(0.3);
            transform: scale(0.3);
}
}
@keyframes load {
0% {
    opacity: 1;
    -webkit-transform: scale(1.3);
            transform: scale(1.3);
}
100% {
    opacity: 0.2;
    -webkit-transform: scale(0.3);
            transform: scale(0.3);
}
}
.vue-auth-box_ .auth-body_ .loading-box_ .loading-gif_ span {
