  -webkit-animation-delay: 0.52s;
          animation-delay: 0.52s;
}
.vue-auth-box_ .auth-body_ .info-box_ {
  position: absolute;
  bottom: 0;
  left: 0;
  width: 100%;
  height: 24px;
  line-height: 24px;
  text-align: center;
  overflow: hidden;
  font-size: 13px;
  background-color: #83ce3f;
  opacity: 0;
  -webkit-transform: translateY(24px);
          transform: translateY(24px);
  -webkit-transition: all 200ms;
  transition: all 200ms;
  color: #fff;
  z-index: 10;
}
.vue-auth-box_ .auth-body_ .info-box_.show {
  opacity: 0.95;
  -webkit-transform: translateY(0);
          transform: translateY(0);
}
.vue-auth-box_ .auth-body_ .info-box_.fail {
  background-color: #ce594b;
}
.vue-auth-box_ .auth-body_ .auth-canvas2_ {
  position: absolute;
  top: 0;
  left: 0;
  width: 60px;
  height: 100%;
  z-index: 2;
}
.vue-auth-box_ .auth-body_ .auth-canvas3_ {
  position: absolute;
  top: 0;
  left: 0;
  opacity: 0;
  -webkit-transition: opacity 600ms;
  transition: opacity 600ms;
  z-index: 3;
}
.vue-auth-box_ .auth-body_ .auth-canvas3_.show {
  opacity: 1;
}
.vue-auth-box_ .auth-body_ .flash_ {
  position: absolute;
  top: 0;
  left: 0;
  width: 30px;
  height: 100%;
  background-color: rgba(255, 255, 255, 0.1);
  z-index: 3;
}
.vue-auth-box_ .auth-body_ .flash_.show {
