  border-radius: 3px;
  box-shadow: 0 0 8px rgba(240, 240, 240, 0.6) inset;
}
.vue-auth-box_ .auth-control_ .range-box .range-text {
  position: absolute;
  top: 50%;
  left: 50%;
  -webkit-transform: translate(-50%, -50%);
          transform: translate(-50%, -50%);
  font-size: 14px;
  color: #b7bcd1;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
  text-align: center;
  width: 100%;
}
.vue-auth-box_ .auth-control_ .range-box .range-slider {
  position: absolute;
  height: 100%;
  width: 50px;
  background-color: rgba(106, 160, 255, 0.8);
  border-radius: 3px;
}
.vue-auth-box_ .auth-control_ .range-box .range-slider .range-btn {
  position: absolute;
  display: -webkit-box;
  display: -webkit-flex;
  display: flex;
  -webkit-box-align: center;
  -webkit-align-items: center;
          align-items: center;
  -webkit-box-pack: center;
  -webkit-justify-content: center;
          justify-content: center;
  right: 0;
  width: 50px;
  height: 100%;
  background-color: #fff;
  border-radius: 3px;
  box-shadow: 0 0 4px #ccc;
  cursor: pointer;
}
.vue-auth-box_ .auth-control_ .range-box .range-slider .range-btn > div {
