  height: 0;
  border-right-color: #6aa0ff;
}
.vue-auth-box_ .auth-control_ .range-box .range-slider .range-btn:hover > div:nth-child(2),
.vue-auth-box_ .auth-control_ .range-box .range-slider .range-btn.isDown > div:nth-child(2) {
